import React, { useState } from 'react'


export const CreateNote = () => {0.
    const [click, setClick] = useState()
    const [data, setData] = useState({
     text: "",
     textarea: "",
    })
// /
    const changeEnput =(e)=>{
        const {name, value} = e.target;
        setData((oldVal)=>{
            return{
                ...oldVal,
                [name]: value,
            }
        })
    }
    // /
    const addItem =()=>{
        setClick(data)
    }
    return (
        <>
        {/* //////////////// */}
            <div className="main_contant">
            
                <input type="text" 
                 placeholder="Title"
                 onChange={changeEnput}
                 name="text"
                />
                <textarea name="textarea"  cols="" rows=""
                placeholder="Enter Your Text..."
                onChange={changeEnput}
                >
                
                </textarea>
                    <i class="fas fa-plus-circle addIcon"
                    onClick={addItem}
                    ></i>
            </div>
            {/* //////// */}
            <div className="note_text">
            <i class="fas fa-trash-alt dltIcon"></i>
          <h3>{data.text}</h3>
          <p>{data.textarea}</p>
        </div>
           
        </>
    )
}
export default CreateNote;
