import React, { useState } from 'react'

const CreateNote = () => {

const {note, setNote} = useState({
    title : "",
    contant : "",
})
    const changeEnput=(event)=>{
        const {name,value} = event.target;
        setNote((preValu) =>{
            return{
                ...preValu,
                [name]: value,
            }
        })
    }
    return (
        <>
            <div className="main_contant">
                <input type="text"
                    placeholder="Title Text"
                    name="title"
                    value={note.title}
                    onChange
                    onChange={changeEnput}
                />
                <textarea 
                    placeholder="Write Your Text..."
                    onChange={changeEnput}
                    name="contant"
                    value={note.contant}
                >
                </textarea>
                <i class="fas fa-plus-circle addIcon"
                    // onClick={addItem}
                    ></i>
            </div>
        </>
    )
}
export default CreateNote;
