import React, {useState} from 'react';
// import "./index.css";

const App = () => {
  const [item, setItems] = useState("");
  const [data, setData] = useState([]);

  const InputEvent =(e)=>{
    setItems(e.target.value)
  }
  
  const clickEvent = () =>{
    setData((preVal)=>{
      return[...preVal, item]
    })
    setItems([])

  }
  return (
    <>
       <h2>ToDo List</h2>
       <input type="text" 
         onChange={InputEvent}
         value={item}
       />
       <span><i class="fas fa-plus-circle" onClick={clickEvent}></i></span>
       <br />
       <br />
       <ol>
       {
         data.map((crnVal) =>{
           return  <li>  <span><i class="fas fa-trash"></i></span>{crnVal}</li>
         })
       }
     
        
       </ol>
          
    </>
  )
}

export default App;
