import React from 'react'
import { useState } from 'react/cjs/react.development'

const CreateNote = (props) => {
    const[item, setItem] = useState({
        title : "",
        contant : ""
    })
    const chngEvent =(event)=>{
      const {name,value} = event.target;
      setItem((preVal) =>{
          return{
              ...preVal,
              [name] : value,
          }
      })
    }
    const adEvent =()=>{
      props.additems(item)
    }
    return (
        <>
        <div className="main_contant">
            <input name="title" value={item.title}
            placeholder="Title"
            onChange={chngEvent}
            />
            <input className="pera"
            name="contant" value={item.contant}
            placeholder="Enter Your Contant"
            onChange={chngEvent}
            />
            <button className="addIcon" onClick={adEvent}>Add</button>
            </div>
        </>
    )
}

export default CreateNote
