import React, { useState } from 'react'

const CreateNote = () => {
const {note, setNote} = useState({

    title : "",
    contant : "",
});
    const changeEnput=(event)=>{
        const {name,value} = event.target;
        setNote((preValu) =>{
            return{
                ...preValu,
                [name]: value,
            };
        });
    };
    return (
        <>
            <div className="main_contant">
                <input type="text" 
                    placeholder="Enter Your Title"
                    name="title"
                    value={note.title}
                    onChange={note.contant}
                />
               <textarea name="" id="" cols="30" rows="10"
               p></textarea>
                <button>

                <i class="fas fa-plus-circle addIcon"
                    // onClick={addItem}
                    ></i>
                </button>
            </div>
        </>
    )
}
export default CreateNote;
