import React, { useState } from 'react'


export const CreateNote = () => {
    const [item, setItem] = useState()
    const [data, setData] = useState({
     text: "",
     textarea: "",
    })

    const changeEnput =(e)=>{
        const {name, value} = e.target;
        setData((oldVal)=>{
            return{
                ...oldVal,
                [name]:value,
            }
        })
    }

    const addItem =()=>{
        setItem(data)
    }
    return (
        <>
        {/* //////////////// */}
            <div className="main_contant">
            
                <input type="text" 
                 placeholder="Title"
                 onChange={changeEnput}
                 name="text"
                 value={data.text}
                />
                <textarea cols="" rows=""
                placeholder="Enter Your Text..."
                onChange={changeEnput}
                name="textarea"
                value={data.textarea}
                > 
                </textarea>
                    <i class="fas fa-plus-circle addIcon"
                    onClick={addItem}></i>
            </div>
            {/* //////// */}
            <div className="note_text">
            <i class="fas fa-trash-alt dltIcon"></i>
            {
                item.map()
            }
          <h3>{data.text}</h3>
          <p>{data.textarea}</p>
        </div>
           
        </>
    )
}
export default CreateNote;
