import { useState, useEffect } from "react";

const App = () => {
  const [onScroll, setOnScroll] = useState(false)

  const myScroll =()=>{
    if (window.pageYOffset > 2) {
      setOnScroll(true)
    }else{
      setOnScroll(false)
    }
  }


  useEffect(() => {
    window.addEventListener("scroll", myScroll);
    return () => {
      window.addEventListener("scroll", myScroll);
    }
  }, [])
  return (
    <>
      <nav >

        <div className={onScroll ? "manu scroll" : "menu"}>
          <ul >
            <li>home</li>
            <li>about</li>
            <li>services</li>
            <li>contact us</li>
          </ul>
        </div>
      </nav>
    </>
  );
}

export default App;
