import React, { useState } from 'react'

const ContactForm = () => {
    const [inputForm, setInputForm] = useState(true)
    const [inputData, setInputData] = useState({
        name: "",
        gmail: "",
        phone: ""
    })
    const inputEvent = (event) => {
        const {name, value} = event.target;
        setInputData((preVal)=>{
            return{
                ...preVal,
                [name] : value,
            }
        })
    }
    
    const formSubmit =(e)=>{
        e.preventDefault(); 
        alert("form submit")
        setInputForm(false)
        return(
            <p className="success"> Got it, I'll contact you shortly, Thanks </p>
        )

    }
    return (
        <>
            <div className="contact_form">
                <form action="" onSubmit={formSubmit}>
                    <input type="text"
                        name="name"
                        placeholder="Name" required
                        value={inputData.name}
                        onChange={inputEvent}
                    />
                    <input type="gmail"
                        name="gmail"
                        placeholder="Gmail" required
                        value={inputData.gmail}
                        onChange={inputEvent}
                    />
                    <input type="numpber"
                        name="phone"
                        placeholder="Phone" required
                        value={inputData.phone}
                        onChange={inputEvent}
                    />
                    <button type="submit"> LET'S TALK
                    {/* &nbsp; */}
                   </button>
                   {/* {
                    inputForm ? "" : <p className="success"> Got it, I'll contact you shortly, Thanks </p>
                   } */}
                </form>
            </div>
        </>
    )
}

export default ContactForm
