import React, { useState } from "react";


const App = () => {
    const crnTime = new Date().toDateString();

    const [cTime, getTime] = useState(crnTime)

    const ClickTime = () =>{
       let crnCTime = new Date().toLocaleTimeString();
        getTime(crnCTime);
    };

    setInterval(ClickTime, 1000);
    return(
   <div>
   <h1> {cTime} </h1>
   {/* <button onClick={ClickTime}>Get Time</button> */}
   </div>
    )
};

export default App;
