  }

  return (
    <>
      <h1>ToDo List</h1>
      <input type="text"
        placeholder="Enter Your Items"
        onChange={changeInput}
        value={state}
      />
      <button onClick={putData}>+</button>
      <ol>
      {
        data.map((crnValue, index)=>{
         return (
           <>
           
           <div className="todoStyle" key={index}
           id={index}>
            <button onClick={dltData}>x</button>
           <li>{crnValue}</li>
           </div>
           </>
         ) 
        })
      }
       
      </ol>
    </>
  )
}

export default App;
