import React, { createContext, useReducer } from 'react';
import  AllData  from "./AllData";
import ContaxtCard from './ContaxtCard';
import { reducer } from './reducer';
import removeItem from './CardData';

export const ContantText = createContext()

const initialState = {
    data: AllData,
    totalAmount: 0,
    totalItems: 0,
};



const Cards = () => {
    // const [data, setData] = useState(AllData);
    const [state, dispatch] = useReducer(reducer, initialState);

    const removeItem =(id)=>{
        return dispatch({
            type: "REMOVE_ITEM",
            payload: id,
        });
       };
       
    return (

        <ContantText.Provider value={{...state , removeItem }}>
        <ContaxtCard />
        </ContantText.Provider>

    )
}

export default Cards;
