import React, { useState } from 'react';
import "./index.css";

const App = () => {
  const [fullName, setFullName] = useState({
  fname: "",
  lname: "",
  gmail: "",
  password: "",

  });

  const inputEvent = (event) => {
    const {value, name} = event.target;
    
    setFullName((preValue) => {
    if(name === "fname"){
      return {
        fname: value,
        lname: preValue.lname,
        gmail: preValue.gmail
      };
    }else if(name === "lname"){
      return {
        fname: preValue.fname,
        lname: value,
        gmail: preValue.gmail
        };
    } else if(name === "password"){
      return {
        fname: preValue.fname,
        lname: preValue.lname,
        gmail: value,
        };
      }
  })

  }
  const subNow = (e) => {
   e.preventDefault()
   alert("form sumbit")
  
  }
  return (
    <>
      <h1>Hello {fullName.fname} {fullName.lname} </h1>
      <p>{fullName.gmail}</p>
      <p>{fullName.password}</p>
      <form action="" onSubmit={subNow}>
      <input type="text"
        placeholder="Enter Your Name"
        name="fname"
        onChange={inputEvent}
        value={fullName.fname}
      />
       <br />
      <br />
      <input type="text"
        placeholder="Enter Your Name"
        name="lname"
        onChange={inputEvent}
        value={fullName.lname}
      />
       <br />
       <br />
      <input type="gmail"
        placeholder="Enter Your Gmail"
        name="gmail"
        onChange={inputEvent}
        value={fullName.gmail}
      />
       <br />
       <br />
      <input type="password"
        placeholder="Enter Your password"
        name="password"
        onChange={inputEvent}
        value={fullName.password}
      />
      <br />
      <br />
      <button >submit</button>
      </form>
    </>
  )
}

export default App;
