import React, { useState } from 'react'

const ContactForm = () => {
    // const [inputForm, setInputForm] = useState(true)
    let [loading, setLoading] = useState(false);
	let [submit, setSubmit] = useState("");

    const [inputData, setInputData] = useState({
        name: "",
        gmail: "",
        phone: ""
    })
    // const inputEvent = (event) => {
    //     const {name, value} = event.target;
    //     setInputData((preVal)=>{
    //         return{
    //             ...preVal,
    //             [name] : value,
    //         }
    //     })
    // }
    
    // const formSubmit =(e)=>{
    //     e.preventDefault(); 
      
    //     <p className="success"> Got it, I'll contact you shortly, Thanks </p>
    // }
    return (
        <>
            <div className="contact_form">
                <form action="" onSubmit={(e)=> inputEvent(e,inputData,setLoading,setSubmit,setInputData)}>
                    <input type="text"
                        name="name"
                        placeholder="Name" required
                        value={inputData.name}
                        onChange={(e) => inputEvent(e, setInputData)}
                    />
                    <input type="gmail"
                        name="gmail"
                        placeholder="Gmail" required
                        value={inputData.gmail}
                        onChange={(e) => inputEvent(e, setInputData)}
                    />
                    <input type="numpber"
                        name="phone"
                        placeholder="Phone" required
                        value={inputData.phone}
                        onChange={(e) => inputEvent(e, setInputData)}
                    />
                 {submit ? (submit) : (
					<button type="submit" disabled={loading}>
						{loading && <i className="fa fa-refresh fa-spin" />}
						&nbsp;LET'S TALK
					</button>
				)}
                </form>
            </div>
        </>
    )
}

export default ContactForm
