import React, { useState } from 'react';
import "./index.css";
import Cards from "./cards"
import Stada from "./Sdata";

const App = () => {
   const [data, setData] = useState(Stada);

  const filterValue =(category)=>{
    const uptodate = Sdata.filter((crnElem) =>{
      return crnElem.category === category;
    });
    setData(uptodate);
  }

  return (
    <>
    <div className="navigation">
      <div className="nav-btn-group">
        <button className="navbtn" onClick={()=> filterValue("breakfast")}>Breakfast</button>
        <button className="navbtn" onClick={()=> filterValue("breakfast")}>Lunch</button>
        <button className="navbtn" onClick={()=> filterValue("breakfast")}>Evening</button>
        <button className="navbtn" onClick={()=> filterValue("breakfast")}>Dinner</button>
      </div>
    </div>
    
   <Cards data = {data}/>

    </>
  )
};

export default App;
