import React,{ useState }  from 'react';
import "./index.css";
import Header from './Header';
import CreateNote from "./CreateNote"
import Node from "./Node"


const App = () => {
  const[data, setData] = useState([])

  const addAllitm =(item)=>{
    setData((oldData) =>{
      return [
        ...oldData, item
      ]
    })
  }

  const dltEvevent =(id)=>{
    setData((oldDlt)=>{
      oldDlt.filter((crnPrv, index) =>{
        return index !==id
      })

    })
  }

  return (
    <>
      <Header/>
      <CreateNote
         additems={addAllitm}
      />
      
      {
        data.map((crnVal, index) =>{
          return <Node 
             key={index}
           id={index}
           title = {crnVal.title}
           contant = {crnVal.contant}
           rmvEvent= {dltEvevent}
          />
           
        })
      }
    </>
  )
}

export default App;
