import React, { useState } from 'react'
import Menu from "./Manu";
import Cards from "./cards";

const App = () => {
  const [data, setData] = useState(Menu);

   const filerValue = (category) => {
   const dataUpdate = Menu.filter((crnElem)=>{
     return crnElem.category === category;
   });
   setData(dataUpdate)
   }
  return (
    <>
    <div className="navigation">
      <div className="nav-btn-group">
        <button className="navbtn" onClick={()=> filerValue("breakfast")}>breakfast</button>
        <button className="navbtn">lunch</button>
        <button className="navbtn">evening</button>
        <button className="navbtn">dinner</button>
      </div>
    </div>
     <Cards data={data}/>
    </>
  )
}

export default App
