//Init
import React, { useState } from "react";

//Importing Modules
import { Circle } from "rc-progress";
import VisibilitySensor from "react-visibility-sensor";

//Importing Actions from External file
import {
	handleCircleOne,
	handleCircleTwo,
	handleCircleThree,
} from "../controllers/work";

//Style
import "../style/Work.css";

//Progress Component
function Progress() {
	//Initializing State
	let [circleOne, setCircleOne] = useState(1);
	let [circleTwo, setCircleTwo] = useState(1);
	let [circleThree, setCircleThree] = useState(1);

	return (
		// Using Visibility Sensor to run progress bar when this component shows to user
		<VisibilitySensor
			partialVisibility
			onChange={(isVisible) => {
				handleCircleOne(isVisible, circleOne, setCircleOne);
				handleCircleTwo(isVisible, circleTwo, setCircleTwo);
				handleCircleThree(isVisible, circleThree, setCircleThree);
			}}
		>
			<div className="progress">
				<div className="progress-block-container">
					<div className="progress-block">
						<i className="fab fa-gitlab"></i>
						<Circle
							className="circle"
							percent={circleOne <= 88 ? circleOne : 88}
							strokeWidth="5"
							strokeColor="#FA225A"
							trailWidth="0"
						/>
					</div>
					<p>PERFORMANCE</p>
				</div>
				<div className="progress-block-container">
					<div className="progress-block">
						<i className="fas fa-shield-alt"></i>
						<Circle
							className="circle"
							percent={circleTwo <= 82 ? circleTwo : 82}
							strokeWidth="5"
							strokeColor="#FA225A"
							trailWidth="0"
						/>
					</div>
					<p>SECURITY</p>
				</div>
				<div className="progress-block-container">
					<div className="progress-block">
						<i className="far fa-gem"></i>
						<Circle
							className="circle"
							percent={circleThree <= 95 ? circleThree : 95}
							strokeWidth="5"
							strokeColor="#FA225A"
							trailWidth="0"
						/>
					</div>
					<p>UI / UX</p>
				</div>
			</div>
		</VisibilitySensor>
	);
}

//Work Section Component
function Work() {
	return (
		<section className="work">
			<div className="work-container">
				<div className="work-heading">
					{/* <h1>{title}</h1> */}
					{/* <h3>{tagline}</h3> */}
					<div className="border"></div>
				</div>
				{/* <p className="work-description">{description}</p> */}
				{/* <Progress /> */}
                
            <div className="progress">
                <div className="progress_block_container">
                    <div className="progress_block">
                        <i class="fab fa-gitlab" aria-hidden="true"></i>
                        <svg class="rc-progress-circle circle" viewBox="0 0 100 100"><path class="rc-progress-circle-trail" d="M 50,50 m 0,-47.5
   a 47.5,47.5 0 1 1 0,95
   a 47.5,47.5 0 1 1 0,-95" stroke="#D9D9D9" stroke-linecap="round" stroke-width="0" fill-opacity="0" style="stroke: rgb(217, 217, 217); stroke-dasharray: 298.451px, 298.451px; stroke-dashoffset: 0px; transition: stroke-dashoffset 0.3s ease 0s, stroke-dasharray 0.3s ease 0s, stroke 0.3s ease 0s, stroke-width 0.06s ease 0.3s;"></path><path class="rc-progress-circle-path" d="M 50,50 m 0,-47.5
   a 47.5,47.5 0 1 1 0,95
   a 47.5,47.5 0 1 1 0,-95" stroke="" stroke-linecap="round" stroke-width="5" opacity="1" fill-opacity="0" style="stroke: rgb(250, 34, 90); stroke-dasharray: 262.637px, 298.451px; stroke-dashoffset: 0px; transition: stroke-dashoffset 0s ease 0s, stroke-dasharray 0s ease 0s, stroke ease 0s, stroke-width ease 0.3s;"></path></svg>
                    </div>
                    <p>PERFORMANCE</p>
                </div>
                <div className="progress_block_container">
                    <div className="progress_block">
                    <i class="fas fa-shield-alt" aria-hidden="true"></i>
                    <svg class="rc-progress-circle circle" viewBox="0 0 100 100"><path class="rc-progress-circle-trail" d="M 50,50 m 0,-47.5
   a 47.5,47.5 0 1 1 0,95
   a 47.5,47.5 0 1 1 0,-95" stroke="#D9D9D9" stroke-linecap="round" stroke-width="0" fill-opacity="0" style="stroke: rgb(217, 217, 217); stroke-dasharray: 298.451px, 298.451px; stroke-dashoffset: 0px; transition: stroke-dashoffset 0.3s ease 0s, stroke-dasharray 0.3s ease 0s, stroke 0.3s ease 0s, stroke-width 0.06s ease 0.3s;"></path><path class="rc-progress-circle-path" d="M 50,50 m 0,-47.5
   a 47.5,47.5 0 1 1 0,95
   a 47.5,47.5 0 1 1 0,-95" stroke="" stroke-linecap="round" stroke-width="5" opacity="1" fill-opacity="0" style="stroke: rgb(250, 34, 90); stroke-dasharray: 244.73px, 298.451px; stroke-dashoffset: 0px; transition: stroke-dashoffset 0s ease 0s, stroke-dasharray 0s ease 0s, stroke ease 0s, stroke-width ease 0.3s;"></path></svg>
                    </div>
                    <p>SECURITY</p>
                </div>
                <div className="progress_block_container">
                    <div className="progress_block">
                    <i class="far fa-gem" aria-hidden="true"></i>
                    <svg class="rc-progress-circle circle" viewBox="0 0 100 100"><path class="rc-progress-circle-trail" d="M 50,50 m 0,-47.5
   a 47.5,47.5 0 1 1 0,95
   a 47.5,47.5 0 1 1 0,-95" stroke="#D9D9D9" stroke-linecap="round" stroke-width="0" fill-opacity="0" style="stroke: rgb(217, 217, 217); stroke-dasharray: 298.451px, 298.451px; stroke-dashoffset: 0px; transition: stroke-dashoffset 0.3s ease 0s, stroke-dasharray 0.3s ease 0s, stroke 0.3s ease 0s, stroke-width 0.06s ease 0.3s;"></path><path class="rc-progress-circle-path" d="M 50,50 m 0,-47.5
   a 47.5,47.5 0 1 1 0,95
   a 47.5,47.5 0 1 1 0,-95" stroke="" stroke-linecap="round" stroke-width="5" opacity="1" fill-opacity="0" style="stroke: rgb(250, 34, 90); stroke-dasharray: 283.529px, 298.451px; stroke-dashoffset: 0px; transition: stroke-dashoffset 0s ease 0s, stroke-dasharray 0s ease 0s, stroke ease 0s, stroke-width ease 0.3s;"></path></svg>
                    </div>
                    <p>UI / UX</p>
                </div>
            </div>
			</div>
		</section>
	);
}

//Exports
export default Work;
