const App = () => {
const [line, SetLine] = useState(false)
  const spnEvent=()=>{
   const showNote = document.getElementById("menu");
   SetLine(false)
   showNote.classList.toggle("active")
  }
 
  return (
    <>
         <div id="menu" >
          <span onClick={spnEvent}>{
            (+ line) ? "-":"+"
          }
          </span>
          <p className="p">Lorem ipsum, dolor sit amet consectetur adipisicing elit. Ex deleniti molestias necessitatibus exercitationem similique aspernatur, sapiente pariatur est tempora perspiciatis earum quaerat blanditiis ut explicabo laudantium quisquam! Id, repellendus architecto.</p>
         </div>
       
    </>
  );
}

export default App;
