import { NavLink } from 'react-bootstrap'

const Footer = () => {
  const [showFooter, setShowFooter] = useState("-3")
  const myFooter=()=>{

      if (window.pageYOffset > 8000) {
        setShowFooter("-1")
      }else{
        setShowFooter("3")
      }
  }

  useEffect(() => {
      window.addEventListener('scroll',  myFooter)
      return () => {
          window.addEventListener('scroll',  myFooter)
          
      }
  }, [])
    return (
