import React, { useState } from 'react'

const ContactForm = () => {
    const [inputForm, setInputForm] = useState(true)
    const [inputData, setInputData] = useState({
        name: "",
        gmail: "",
        phone: ""
    })
    const inputEvent = (event) => {
        const { name, Value } = event.target;
        setInputData((preVal)=>{
            return{
                preVal,
                [name] : Value,
            }
        })
    }
    inputForm("")
    
    const formSubmit =(e)=>{
        e.preventDefault();
        
        setInputForm(false)
        // setInputData([])
    }
    return (
        <>
            <div className="contact_form">
                <form action="" onSubmit={formSubmit}>
                    <input type="text"
                        name="name"
                        placeholder="Name" required
                        value={inputData.name}
                        onChange={inputEvent}
                    />
                    <input type="gmail"
                        name="gmail"
                        placeholder="Gmail" required
                        value={inputData.gmail}
                        onChange={inputEvent}
                    />
                    <input type="numpber"
                        name="phone"
                        placeholder="Phone" required
                        value={inputData.phone}
                        onChange={inputEvent}
                    />
                    <button type="submit">
                    {/* &nbsp; */}
                   {
                    inputForm ? "LET'S TALK" : <p className="success"> Got it, I'll contact you shortly, Thanks </p>
                   }
                   </button>
                </form>
            </div>
        </>
    )
}

export default ContactForm
