import React, { useState } from "react";

const App = () =>{
    const green = "green";
    const [bg, setBg] = useState(green)

    const bgChange = () =>{
        let newBg = "black";
        bg(newBg);
        setBg("och!!");
    }
    return(
        <>
        <div style = {{ backgroundColor: bg}}>
            <button onClick={bgChange}>Click Me</button>
        </div>
        </>
    )
}
export default App;
