import React, { useState } from 'react'
import Menu from "./Manu";
import Cards from "./cards";
import Navbar from './Navbar';

const uniData = [...new Set(Menu.map((crnElem) =>{
  return crnElem.category;
})
),"All",
];

const App = () => {
  const [data, setData] = useState(Menu);
  const [navBtn, setNavBtn] = useState(uniData);

   const filerValue = (category) => {
     if(uniData === "All"){
      data("All");
      return;
     }
   const dataUpdate = Menu.filter((crnElem)=>{
     return crnElem.category === category;
   });
   setData(dataUpdate)
   }
  return (
    <>
    <Navbar filerValue= {filerValue} navBtn={navBtn}/>
     <Cards data={data}/>
    </>
  )
}

export default App
