import React, { useState } from 'react';
import "./index.css";

const App = () => {
  const [data, setData] = useState({
    fname: "",
    lname: "",
    gmail: ""
  });

  const inputEvent = (e) => {
    var value = e.target.value;
    var name = e.target.name;
    setData((preValue) => {
      if (name === "fname") {  
         return{

         fname : value,
         lname : preValue.lnam,
         gmail : preValue.gmail,
       }
     }
    })

  }
  const subNow = () => {
   
  }
  return (
    <>
      <h1>Hello {data.fname} {data.gmail} 
      </h1>
      <p></p>
      <p>{data.gmail}</p>
      <input type="text"
        placeholder="Enter Your Name"
        name="fname"
        onChange={inputEvent}
        value={data.fname}
      />
       <br />
      <br />
      <input type="lname"
        placeholder="Enter Your Name"
        name="lname"
        onChange={inputEvent}
        value={data.lname}
      />
       <br />
       <br />
      <input type="gmail"
        placeholder="Enter Your Name"
        name="gmail"
        onChange={inputEvent}
        value={data.gmail}
      />
      <br />
      <br />
      <button onClick={subNow}>submit</button>
    </>
  )
}

export default App;
