import React, { useState } from 'react';
import "./index.css";
import Cards from "./cards"
import Sdata from "./Sdata";
import Navbar from "./Navbar"

const uniData = menu.map((crnElem)=>{
  return crnElem.category;
})

const App = () => {
  const [data, setData] = useState(Sdata);

  const filterValue = (category) => {
    const uptodate = Sdata.filter((crnElem) => {
      return crnElem.category === category;
    });
    setData(uptodate);
  }

  return (
    <>
      <Navbar filterValue ={ filterValue} />
      <Cards data={data} />

    </>
  )
};

export default App;
