import React, { useState } from 'react';
import "./index.css";

const App = () => {
const [state, setstate] = useState("")
const [data, setData] = useState([])
  const changeInput = (e) => {
    setstate(e.target.value);
  
  }
  const putData = () => {
    setData((prevalu)=>{
      return[...prevalu, state]
    })
    setstate([])
  }

  const dltData =(id)=>{
    setData((oldData) =>{
       return oldData.filter((crnData, index)=>{
         return index !== id;
       })
    })
  }

  return (
    <>
      <h1>ToDo List</h1>
      <input type="text"
        placeholder="Enter Your Items"
        onChange={changeInput}
        value={state}
      />
      <button onClick={putData}>+</button>
      <ol>
      {
        data.map((crnValue,index)=>{
         return (
           <>    
         
           <div className="todoStyle"   >
            <button onClick={()=>{
              return <dltData
                key={ index }

              />

            }}>x</button>
           <li>{crnValue}</li>
           </div>
           </>
         ) 
        })
      }
       
      </ol>
    </>
  )
}

export default App;
