import React,{ useState } from 'react'

const CreateNote = (props) => {
    const [hide, setHide] = useState(false)
    const[item, setItem] = useState({
        title : "",
        contant : ""
    })
    const chngEvent =(event)=>{
      const {name,value} = event.target;
      setItem((preVal) =>{
          return{
              ...preVal,
              [name] : value,
          }
      })
    }
    const adEvent =()=>{
      props.additems(item)
      setItem({
        title : "",
        contant : ""
    })
    }
    return (
        <>
        <div className="main_contant" onClick={hideEve}>

        {hide ?
            <input name="title" value={item.title}
            placeholder="Title"
            onChange={chngEvent}
            autoComplete="off"
            />
            : none }
            <input className="pera"
            name="contant" value={item.contant}
            placeholder="Enter Your Contant"
            onChange={chngEvent}
            />
            {hide ?
            <button className="addIcon" onClick={adEvent}>Add</button>
            : none }
            </div>
        </>
    )
}

export default CreateNote
