import React, { useState } from 'react'

const CreateNote = () => {
const {allData, setAllData} = useState({
    title: "",
    contant: "",
})
    const changeEnput=(event)=>{
        const [name,value] = event.target;
        setAllData((preValu) =>{
            return{
                ...preValu,
                [name]: value,
            }
        })
    }
    return (
        <>
            <div className="main_contant">

                <input type="text"
                    placeholder="Title Text"
                    onChange={changeEnput}
                    name={title}
                    value={allData.title}
                />
                <textarea cols="" rows=""
                    placeholder="Write Your Text..."
                    onChange={changeEnput}
                    name={contant}
                    value={allData.contant}
                >
                </textarea>
                <i class="fas fa-plus-circle addIcon"
                    // onClick={addItem}
                    ></i>
            </div>
        </>
    )
}
export default CreateNote;
