import React, { useState } from 'react';
import img from "../src/image/list.png"

const App =()=> {
  const [data, SetData] = useState("");

  const putData =(e)=>{
    const [name ,value] = e.targer.value;

    SetData((preval)=>{
      return{ 
        ...preVal,
        [name] : value,


      }
    })
  }
    return (
     <>
     <div className="main_div">
       <div className="center_div">
          <div className="data">
            <img src={img} alt="" />
            <h3>✍️ ToDo List Items</h3>
            <div className="in_val">
              <input type="text" 
                placeholder="Write Add items"
                value={data}
                name="data"
              />
              <button className="addBtn">
              <i class="far fa-plus-square" title="Add Items" style={{width: "27px"}}></i>
              </button>
            </div>
            <div className="in_show">
            <input type="text" 
                placeholder="Write Add items"
                value={data}
                name="data"
                onChange={putData}
              />
            </div>
            {/* <div className="data_enter">
            <i class="far fa-trash-alt dlt" title="Deleted"></i>
            <div className="data_value" >
            <div className="colect_data" onChange={putData}>
              <h2 >{data}</h2>
            </div>

            </div>
            </div> */}
          </div>
       </div>
     </div>

     </>
    )
};

export default App;
