    <>
      <h1>Hello {data.fname} </h1>
      <p>{data.lname}</p>
      <p>{data.gmail}</p>
      <input type="fname"
        placeholder="Enter Your Name"
        name="fname"
        onChange={inputEvent}
        value={data.fname}
      />
      <input type="lname"
        placeholder="Enter Your Name"
        name="lname"
        onChange={inputEvent}
        value={data.lname}
      />
      <input type="gmail"
        placeholder="Enter Your Name"
        name="gmail"
        onChange={inputEvent}
        value={data.gmail}
      />
      <br />
      <br />
      <button onClick={subNow}>submit</button>
    </>
  )
}

export default App;
